import React, { useState } from "react";
import './App.css';

export default function App() {
  const [ticker, setTicker] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [riskLevel, setRiskLevel] = useState("medium");
  const [budget, setBudget] = useState("");  // Added budget state
  const [recommendation, setRecommendation] = useState(null);
  const [error, setError] = useState("");

  const handleSubmit = async () => {
    setError("");
    try {
      const response = await fetch("http://localhost:8000/recommend", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          ticker,
          start_date: startDate,
          end_date: endDate,
          risk_level: riskLevel,
          budget,  // Passing budget to backend
        }),
      });

      if (!response.ok) {
        // If the response is not 2xx, throw an error
        throw new Error(`Error: ${response.statusText}`);
      }

      const data = await response.json();

      if (data && data.ticker) {
        setRecommendation(data);
      } else {
        setError("Invalid response from server");
        setRecommendation(null);
      }
    } catch (error) {
      console.error("Error during fetch:", error);
      setError(error.message);
      setRecommendation(null);
    }
  };

  return (
    <div className="container">
      <h1>Stock Recommendation System üìà</h1>
      <div className="form">
        {/* Input fields stacked vertically */}
        <input
          type="text"
          placeholder="Ticker (e.g., TSLA) üìä"
          value={ticker}
          onChange={(e) => setTicker(e.target.value)}
        />
        
        <input
          type="text"
          placeholder="Enter your Budget (e.g., 10000) üíµ"
          value={budget}
          onChange={(e) => setBudget(e.target.value)}
        />

        <input
          type="date"
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
        />
        
        <input
          type="date"
          value={endDate}
          onChange={(e) => setEndDate(e.target.value)}
        />

        <select
          value={riskLevel}
          onChange={(e) => setRiskLevel(e.target.value)}
        >
          <option value="low">Low Risk üòå</option>
          <option value="medium">Medium Risk ‚öñÔ∏è</option>
          <option value="high">High Risk ‚ö°</option>
        </select>
        
        <button onClick={handleSubmit}>Get Recommendation üöÄ</button>
      </div>

      {error && <div className="error">{error}</div>}

      {recommendation && (
        <div className="result">
          <h3>Recommendation for {recommendation.ticker} üìä</h3>
          <p>Average Return: {(recommendation.average_return * 100).toFixed(2)}% üìà</p>
          <p>Volatility: {(recommendation.volatility * 100).toFixed(2)}% üå™Ô∏è</p>
          <p>
            {recommendation.manipulated
              ? "‚ö†Ô∏è Potential Manipulation Detected"
              : "‚úÖ No Manipulation Detected"}
          </p>
          
          {/* Graph for stock price over time */}
          {recommendation.graph_url && (
            <div className="stock-graph">
              <img src={recommendation.graph_url} alt="Stock Price Graph" />
            </div>
          )}

          {/* Budget Information */}
          {recommendation.suggested_investment && (
            <p>Suggested Investment Amount: ${recommendation.suggested_investment} üí∞</p>
          )}
        </div>
      )}
    </div>
  );
}
